import React from "react";
import Hero from "../../components/Hero/Hero";
import Main from "../../components/Main/Main";
import Comments from "../../components/Comments/Comments";
import SideVideo from "../../components/SideVideo/SideVideo";
import data from '../../data/video-details.json';
import {BrowserRouter ,Router, Route, Switch} from 'react-router-dom';
import axios from "axios";

// API KEY "api_key": "cfddcce4-2fac-4b3f-8b69-8f2abdd9b1d9"

class Home extends React.Component {
    state = {
        sideVideos: [],
        main: [],
        comments: [],
        allVideos:[]
    };

componentDidMount(){
    axios
    .get(`https://project-2-api.herokuapp.com/videos?api_key=cfddcce4-2fac-4b3f-8b69-8f2abdd9b1d9`)    //Get all side videos info 
    .then (response =>{
        const allVideos = response.data;   

        axios
        .get (`https://project-2-api.herokuapp.com/videos/84e96018-4022-434e-80bf-000ce4cd12b8?api_key=cfddcce4-2fac-4b3f-8b69-8f2abdd9b1d9`)
        .then(response=> {
            let sideVideos = allVideos.filter(                               //Get sidevideo lists except main video (1st)
                video => video.id !=="84e96018-4022-434e-80bf-000ce4cd12b8"    
            );
            const main = response.data;
            const comments = response.data.comments;
            this.setState({sideVideos, main, comments, allVideos});
        })
    })
}

componentDidUpdate(prevProps){
    if(prevProps.match !== this.props.match){
        axios
        .get(`https://project-2-api.herokuapp.com/videos/{this.props.match.params.id}?api_key=cfddcce4-2fac-4b3f-8b69-8f2abdd9b1d9`)

    }
}


