render(){
return (
    <>
    <Hero mainDisplayId={this.state.main[0].image} />
    <Main mainDisplayId={this.state.main}/>
    <Comments  mainDisplayId={this.state.comments}/>
    <SideVideo  displayVideo={this.changeId} mainDisplayId={this.state.sideVideos}/>
    </>
 )
}
}


export default Home;

