import React from "react";
import "./hero.scss"
import pause from "../../assets/Images/Icons/pause.svg"
import play from "../../assets/Images/Icons/play.svg"
import fullscreen from "../../assets/Images/Icons/fullscreen.svg"
import volumeOff from "../../assets/Images/Icons/volume_off.svg"
import volumeup from "../../assets/Images/Icons/volume_up.svg"

import scrub from "../../assets/Images/Icons/scrub.svg"


const Hero = ({hero}) => {

    const heroContent = hero.map((object,id)=>{
        return(
            <div key ={object.id} className="hero">
                <video  className="hero__video" poster={object.image}  type="video/mp4" ></video>


            </div>
        );
    })

    return <div>{heroContent}</div>
}

export default Hero;
