import React from "react";
import "./hero.scss"
import video from "../../assets/video/sample.mp4"

const Hero = ({hero}) => {

    const heroContent = hero.map((object,id)=>{
        return(
            <div className="hero">
                <video src={video} className="hero__video" poster={object.image} duration={object.duration} type="video/mp4" controls>
                </video>
            </div>
        );
    })

    return <div>{heroContent}</div>
}

export default Hero;
