import React from "react";
import Header from './components/Header/Header';
import Hero from './components/Hero/Hero';
import Main from './components/Main/Main';
import Comments from './components/Comments/Comments';
import Sidepage from "./components/SideVideo/SideVideo";
import data from './data/video-details.json';


class App extends React.Component {
  constructor(){
    super()
    this.state = {
      mainVideoId: data[0].id

    }

  }
  changeId = (id) => {
    this.setState({mainVideoId: id})  
    // mainVideoId의 값을  id로 바꿔준다는건데 이게 이해가 잘안가는게 위에 this.state에서는 
    // mainVideoId: data[0].id 잖아 데이터에서 첫번째 array의 id의 값을 지정시켜준건데 
    // 이게 그냥 mainVideoId: id 이게 아니라 mainVideoId: data.id로 해줘야되는거 아니야?
    // 분명아까는 이해갔는데 나 혼자 다시보니까 이해가 잘안가네..

  }

  render(){

  return (
    <>
    <Header />
    <Hero />
    <Main mainDisplayId={this.state.mainVideoId}/>
    <Comments  mainDisplayId={this.state.mainVideoId}/>
    <Sidepage  displayVideo={this.changeId} mainDisplayId={this.state.mainVideoId}
    />
    </>
  );  
}
}

export default App;
